import React from 'react'
import styles from './styles.module.css'

const removeScript = (scriptToremove) => {
  let allsuspects = document.getElementsByTagName("script");
  for (let i = allsuspects.length; i >= 0; i--) {
    if (allsuspects[i] && allsuspects[i].getAttribute("src") !== null
      && allsuspects[i].getAttribute("src").indexOf(`${scriptToremove}`) !== -1) {
      allsuspects[i].parentNode.removeChild(allsuspects[i])
    }
  }
}

const appendScript = (scriptToAppend) => {
  const script = document.createElement("script");
  script.src = scriptToAppend;
  script.async = true;
  document.body.appendChild(script);
}

const appendStyle = (styleToAppend) => {
  const link = document.createElement("link");
  link.href = styleToAppend;
  link.rel = "stylesheet";
  link.type = "text/css"
  document.body.appendChild(link);
}
const removeStyle = (scriptToremove) => {
  let allsuspects = document.getElementsByTagName("link");
  for (let i = allsuspects.length; i >= 0; i--) {
    if (allsuspects[i] && allsuspects[i].getAttribute("href") !== null
      && allsuspects[i].getAttribute("href").indexOf(`${scriptToremove}`) !== -1) {
      allsuspects[i].parentNode.removeChild(allsuspects[i])
    }
  }
}

export class VimonialSlider extends React.Component {

  constructor(props) {}
  componentDidMount() {
    appendScript("/path/to/resource.js");
    appendStyle("/path/to/resource.js");
  }
  componentDidUnmount() {
    removeScript("/path/to/resource.js")
    removeStyle("/path/to/resource.js")
  }

  render() {
    return <div><p>VIMONIAL</p><div
      id="vimonial-carousel"
      data-add-review="{{ block.settings.add_review }}"
      data-slide-gap="{{ block.settings.slide_gap }}"
      data-autoplay="{{ block.settings.autoplay }}"
      data-popup="false"
      data-display-quote="false"
      data-display-card="false"
      data-display-discount="false"
      data-width="{{ block.settings.width }}"
      data-width-mobile="{{ block.settings.width_mobile }}"
      data-shadow-size="{{ block.settings.shadow_size }}"
      data-color-play-button="{{ block.settings.color_play_button }}"
      data-color-play-button-inner="{{ block.settings.color_play_button_inner }}"
      data-order-by="{{ block.settings.order_by }}"
      data-border-size="{{ block.settings.border_size }}"
      data-border-color="{{ block.settings.border_color }}"
      data-load-background-color="{{ block.settings.load_background_color }}"
      data-align-items="{{ block.settings.align_items }}"
      data-overflow="{{ block.settings.overflow }}"
      data-shop-url="{{ shop.url }}"
      data-campaign-id="{{ block.settings.campaign_id }}"
      data-shop-id="{{ shop.id }}"
      data-product-id={this.props.productId || ""}></div>
</div>
  }

}
